pragma solidity 0.8.9;

library Config {
    /// Fixed at deployment time
    struct Deployment {
        // Name of the NFT contract.
        string name;
        // Symbol of the NFT contract.
        string symbol;
        // The contract owner address. If you wish to own the contract, then set it as your wallet address.
        // This is also the wallet that can manage the contract on NFT marketplaces.
        address owner;
        // If true, tokens may be burned by owner. Cannot be changed later.
        bool tokensBurnable;
    }

    /// Updatable by admins and owner
    struct Runtime {
        // Metadata base URI for tokens, NFTs minted in this contract will have metadata URI of `baseURI` + `tokenID`.
        // Set this to reveal token metadata.
        string baseURI;
        // If true, the base URI of the NFTs minted in the specified contract can be updated after minting (token URIs
        // are not frozen on the contract level). This is useful for revealing NFTs after the drop. If false, all the
        // NFTs minted in this contract are frozen by default which means token URIs are non-updatable.
        bool metadataUpdatable;
        // If true, tokens may be transferred by owner. Default is true. Can be only changed to false.
        bool tokensTransferable;
        // Secondary market royalties in basis points (100 bps = 1%)
        uint256 royaltiesBps;
        // Address for royalties
        address royaltiesAddress;
    }
}","keccak256":"0xdfb7dacbdcf84a5369cdd753e049e91969acb1bd4342b12b3e09538b52e3c41f","license":"MIT"},"contracts/lib/GranularRoles.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import "@openzeppelin/contracts/access/AccessControl.sol";

abstract contract GranularRoles is AccessControl {
    // Roles list
    // Admin role can have 2 addresses: 
    // one address same as (_owner) which can be changed 
    // one for NFTPort API access which can only be revoked
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    // Following roles can have multiple addresses, can be changed by admin or update contrac role
    bytes32 public constant MINT_ROLE = keccak256("MINT_ROLE");
    bytes32 public constant UPDATE_CONTRACT_ROLE = keccak256("UPDATE_CONTRACT_ROLE");
    bytes32 public constant UPDATE_TOKEN_ROLE = keccak256("UPDATE_TOKEN_ROLE");
    bytes32 public constant BURN_ROLE = keccak256("BURN_ROLE");
    bytes32 public constant TRANSFER_ROLE = keccak256("TRANSFER_ROLE");

    struct RolesAddresses {
        bytes32 role;
        address[] addresses;
        bool frozen;
    }

    address internal _owner;
    address internal _nftPort;

    mapping(bytes32 => address[]) internal _rolesAddressesIndexed; // Used to get roles enumeration
    mapping(bytes32 => bool) internal _rolesFrozen;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    function owner() public view returns (address) {
        return _owner;
    }

    function transferOwnership(address newOwner) public {
        require(newOwner != _owner, "Already the owner");
        require(msg.sender == _owner, "Only owner can transfer ownership");
        _revokeRole(ADMIN_ROLE, _owner);
        address previousOwner = _owner;
        _owner = newOwner;
        _grantRole(ADMIN_ROLE, _owner);
        emit OwnershipTransferred(previousOwner, newOwner);
    }

    function revokeNFTPortPermissions() public onlyRole(ADMIN_ROLE) {
        _revokeRole(ADMIN_ROLE, _nftPort);
        _nftPort = address(0);
    }

    // Admin role has all access granted by default 
    function hasRole(bytes32 role, address account) public view virtual override returns (bool) {
        return super.hasRole(ADMIN_ROLE, account) || super.hasRole(role, account);
    }

    function _initRoles(address owner, RolesAddresses[] memory rolesAddresses) internal {
        _owner = owner;
        _nftPort = msg.sender;
        _grantRole(ADMIN_ROLE, _owner);
        _grantRole(ADMIN_ROLE, _nftPort);

        for (uint256 roleIndex = 0; roleIndex < rolesAddresses.length; roleIndex++) {
            bytes32 role = rolesAddresses[roleIndex].role;
            require(_regularRoleValid(role), "GranularRoles: Invalid rolesAddresses");
            for(uint256 addressIndex = 0; addressIndex < rolesAddresses[roleIndex].addresses.length; addressIndex++) {
                _grantRole(role, rolesAddresses[roleIndex].addresses[addressIndex]);
                _rolesAddressesIndexed[role].push(rolesAddresses[roleIndex].addresses[addressIndex]);
            }
            if (rolesAddresses[roleIndex].frozen) {
                _rolesFrozen[role] = true;
            }
        }
    }

    function _updateRoles(RolesAddresses[] memory rolesAddresses) internal {
        if (rolesAddresses.length > 0) {
            require(hasRole(ADMIN_ROLE, msg.sender), "Granular roles: only ADMIN_ROLE can change permissions");
            for (uint256 roleIndex = 0; roleIndex < rolesAddresses.length; roleIndex++) {
                bytes32 role = rolesAddresses[roleIndex].role;
                require(_regularRoleValid(role), "GranularRoles: Invalid rolesAddresses");
                require(!_rolesFrozen[role], "GranularRoles: One of roles is frozen");
                for(uint256 addressIndex = 0; addressIndex < _rolesAddressesIndexed[role].length; addressIndex++) {
                    _revokeRole(role, _rolesAddressesIndexed[role][addressIndex]);
                }
                delete _rolesAddressesIndexed[role];
                for(uint256 addressIndex = 0; addressIndex < rolesAddresses[roleIndex].addresses.length; addressIndex++) {
                    _grantRole(role, rolesAddresses[roleIndex].addresses[addressIndex]);
                    _rolesAddressesIndexed[role].push(rolesAddresses[roleIndex].addresses[addressIndex]);
                }
                if (rolesAddresses[roleIndex].frozen) {
                    _rolesFrozen[role] = true;
                }
            }
        }
    }

    function _regularRoleValid(bytes32 role) internal returns (bool) {
        return 
            role == MINT_ROLE || 
            role == UPDATE_CONTRACT_ROLE ||
            role == UPDATE_TOKEN_ROLE ||
            role == BURN_ROLE ||
            role == TRANSFER_ROLE;
    }
}","keccak256":"0x1265358bbae5af64b1cc1faeacce606efdaad32e91ffadc5030739f43a78d764","license":"MIT"}},"version":1}
